	ChildObjectTypeService.Update(entity.ChildObjectSource);
	StreetService.Update(entity);
}
catch (Exception e)
{
	if (tm != null && tm.IsOpen) tm.Rollback();
	if (DomainUtil.HandleException(e, name)) throw;
}

